INSERT INTO TS31(tipo_renta, descripcion, articulo_ley, codigo_ocbe) VALUES(7, 'Dividendos y cualquier otra forma de distribucion de utilidades, cuando la empresa que los distribuye, pague y acredite se encuentre domiciliada en el pais, o cuando el fondo de inversion, patrimonio fideicometido o fiduciario banacario, fondo de pensiones, que lo distribuya, pague o acredite, se encuentre constituido o establecido en el pais','Art. 9° d)','10');
INSERT INTO TS31(tipo_renta, descripcion, articulo_ley, codigo_ocbe) VALUES(8, 'Los rendimientos de los ADR y GDRs que tengan como subyacentes acciones emitidas por empresas no domiciliadas','Art. 9° d)','10');
